// import React from "react";
import {React, useRef, useState} from "react";
import Form from "react-bootstrap/Form";
import InputGroup from "react-bootstrap/InputGroup";
import FormControl from "react-bootstrap/FormControl";
// import Button from "react-bootstrap/Button";




// import React, { useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import { baseUrl } from "../URL/AppConstants";

import { Button, Col, Container,  Row } from "react-bootstrap";
const Register = () => {
  const [profile, setprofile] = useState({
    // address: {
    //   city: " ",
    //   country: " ",
    //   house: " ",
    //   state: " ",
    //   street: " ",
    // },
    contactNumber: "",
    date: "",
    email: "",
    firstName: "",
    gender: "",
    lastName: "" ,
    password: "",
    username: "",
  });
  const [formErrors, setFormErrors] = useState({});
  const handleChange = (event) => {
    setprofile((profile) => ({
      ...profile,
      [event.target.name]: event.target.value,
    }));
  };
  const handleSubmit = () => {
    let errors = {};
    if (!profile. firstName) {
      errors["  firstNameErr"] = "Enter First Name";
    }
    if (!profile.lastName) {
      errors["lastNameErr"] = "Enter Last Name";
    }
    if (!profile.email) {
      errors["emailErr"] = "Enter Email Id";
    }
    if (!profile.contactNumber) {
      errors["contactNumberErr"] = "Enter Contact Number";
    }
    if (!profile.date) {
      errors["dateErr"] = "Enter Date OF Birth";
    }
    if (!profile.username) {
      errors[" usernameErr"] = "Enter  UserName";
    }
    if (!profile.password) {
      errors["passwordErr"] = "Enter Password";
    }
    if (!profile. gender){
        errors[" genderErr"] = "Enter Gender";
    }

    setFormErrors(errors);
    const noErrors = Object.keys(errors).length === 0;
    if (noErrors) {
      const palyload = {
         firstName: profile.firstName  ,
         lastName: profile.lastName,
        email: profile.email,
        contactNumber: profile.contactNumber,
        date: profile.date,
        gender: profile.gender,
        username: profile.username,
        password: profile.password,
        // phNumber: user.phNumber,
        // education: user.education,
        // village: user.village,
        // city: user.city,
        // state: user.state,
        // pincode: user.pincode,
        // dob: user.dob,
        // gender: user.gender,
        // income: user.income,
        // userName: user.userName,
        // password: user.password,
        // role: "",
      };

      axios
        .post(baseUrl + "profile/save", palyload)
        .then((resp) =>
          alert("profile is saved with id:" + resp.data.profileId)
        );
    }
  };

  return (
    <Container>
      <Row>
        <Col>
          <div>
            <h1 style={{ color: "yellow" }}>
              <b>Please Enter The Following Details</b>
            </h1>
            <div
              className="Login"
              class="row h-100 justify-content-center align-items-center"
            >
              <div className="col-10 col-md-8 col-lg-3">
                <div className="form-group">
                  <div>
                    <label>First Name</label>
                    <input
                      placeholder="first name"
                      className="form-control"
                      type="text"
                      name="firstName"
                      value={profile.firstName}
                      onChange={handleChange}
                    />
                    {formErrors.firstNameErr && (
                      <div style={{ color: "red", paddingBottom: 10 }}>
                        {formErrors.firstNameErr}
                      </div>
                    )}
                  </div>
                  <div className="form-group">
                    <label>Lastst Name</label>
                    <input
                      placeholder="last name"
                      className="form-control"
                      type="text"
                      name="lastName"
                      value={profile.lastName}
                      onChange={handleChange}
                    />
                    {formErrors.lastNameErr && (
                      <div style={{ color: "red", paddingBottom: 10 }}>
                        {formErrors.lastNameErr}
                      </div>
                    )}
                  </div>
                  <div className="form-group">
                    <label> Email Id</label>
                    <input
                      placeholder="email id"
                      className="form-control"
                      type="email"
                      name="email"
                      value={profile.email}
                      onChange={handleChange}
                    />
                    {formErrors.emailErr && (
                      <div style={{ color: "red", paddingBottom: 10 }}>
                        {formErrors.emailErr}
                      </div>
                    )}
                  </div>
                  <div className="form-group">
                    <label>Contact Number</label>
                    <input
                      placeholder="phone number"
                      className="form-control"
                      type="number"
                      name="contactNumber"
                      value={profile.contactNumber}
                      onChange={handleChange}
                    />
                    {formErrors.contactNumberErr && (
                      <div style={{ color: "red", paddingBottom: 10 }}>
                        {formErrors.contactNumberErr}
                      </div>
                    )}
                  </div>

                  <div className="form-group">
                    <label> Date Of Birth</label>
                    <input
                      className="form-control"
                      type="date"
                      name="date"
                      value={profile.date}
                      onChange={handleChange}
                    />
                    {formErrors.dateErr && (
                      <div style={{ color: "red", paddingBottom: 10 }}>
                        {formErrors.dateErr}
                      </div>
                    )}
                  </div>
                  <div className="form-group">
                    <label> Gender</label>
                    <input
                      placeholder="gender"
                      className="form-control"
                      type="text"
                      name="gender"
                      value={profile.gender}
                      onChange={handleChange}
                    />
                    {formErrors.genderErr && (
                      <div style={{ color: "red", paddingBottom: 10 }}>
                        {formErrors.genderErr}
                      </div>
                    )}
                  </div>

                  <div className="form-group">
                    <label>UserName</label>
                    <input
                      placeholder="user name"
                      className="form-control"
                      type="text"
                      name="userName"
                      value={profile.userName}
                      onChange={handleChange}
                    />
                    {formErrors.userNameErr && (
                      <div style={{ color: "red", paddingBottom: 10 }}>
                        {formErrors.userNameErr}
                      </div>
                    )}
                  </div>
                  <div className="form-group">
                    <label>Password</label>
                    <input
                      placeholder="password"
                      className="form-control"
                      type="password"
                      name="password"
                      value={profile.password}
                      onChange={handleChange}
                    />
                    {formErrors.passwordErr && (
                      <div style={{ color: "red", paddingBottom: 10 }}>
                        {formErrors.passwordErr}
                      </div>
                    )}
                  </div>

                  <div>
                    <button className="main" onClick={handleSubmit}>
                      Save
                    </button>
                  </div>

                  <p>
                    <i className="fount">If you are not login, </i>
                    <Link to={"/login"}>
                      <i>LOGIN</i>, <Link to="/allproducts">HOME </Link>
                    </Link>
                  </p>
                </div>
              </div>
            </div>
          </div>
        </Col>
        {/* <Col lg={7} md={6} sm={12} className='back-img-login'>
<img className='img-back' src={img3} alt='backgroung' />
</Col> */}
      </Row>
    </Container>
  );
};

export default Register;
