import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
 //import './Navbar.css';


function Navbar() {
  const [click, setClick] = useState(false);
  const [button, setButton] = useState(true);

  const handleClick = () => setClick(!click);
  const closeMobileMenu = () => setClick(false);

  const showButton = () => {
    if (window.innerWidth <= 960) {
      setButton(false);
    } else {
      setButton(true);
    }
  };
 

  useEffect(() => {
    showButton();
  }, []);

  window.addEventListener('resize', showButton);


  return (
    <div>
    <br/>
      <nav className="navbar navbar-expand navbar-success bg-success">
      <div className="navbar-nav mr-auto">
        <Link to={"/"} className="navbar-brand">
       <b><i>SHOPPING</i> </b>
        </Link>
        
            
          {/* <li className="nav-item">
            <Link to={"/"} className="nav-link">
            <b><i>HOME</i> </b>
            </Link>
          </li> */}

          {/* <li className="nav-item">
            <Link to={"/Services"} className='nav-link'>
              Packages
            </Link>
          </li> */}
          {/* <li className="nav-item">
            <Link to={"/About"} className='nav-link'>
            <b><i>ABOUT </i> </b>
            </Link>
          </li> */}

          <li className="nav-item">
            <Link to={"/adminproducts"} className='nav-link'>
            <b><i>PRODUCTS</i> </b>
            </Link>
          </li>
          <li className="nav-item">
            <Link to={"/addproduct"} className='nav-link'>
             <b><i>Add products</i></b>
            </Link>
          </li>

          {/* <li className="nav-item">
            <Link to={"/addpacks"} className='nav-link'>
              Add Packages
            </Link>

            
          </li> */}


          {/* <li className="nav-item">
            <Link to={"/orderdetails"} className='nav-link'>
             Order Details
            </Link>
          </li> */}

          
        </div>

        <div className="ms-auto pe-md-5 navbar-nav">
        
          <li className='nav-item'>
            <Link to='/login' className='nav-link'>
            <b><i>LOG-OUT</i> </b>
            </Link>
          </li>
         
        </div>



      </nav>
    </div>
  );
}
export default Navbar;
