// import React from 'react'
import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router";
import { Link } from "react-router-dom";
import { Button, Col, Container,  Row } from "react-bootstrap";


function AddProduct() {

    const [product, setProduct] = useState({
     
        productId: "",
        name: "",
        price: "",
        catagory:"",
        description:"",
        image:""
      });
    
      const navigate = useNavigate();
      const [formErrors, setFormErrors] = useState({});
      const handleChange = (event) => {
        setProduct((product) => ({
            ...product,
              [event.target.name]: event.target.value,
             
            }));
          };
          const handleSubmit = () => {
           
            let errors = {};
            if (!product.name) {
                errors["nameErr"] = "product Name is required";
              }
              if (!product.price) {
                  errors["priceErr"] = "Price is required";
                }
              if (!product. catagory) {
                errors[" catagoryErr"] = " Catagory is required";
              }
              if (!product.description){
                  errors["descriptionErr"]="description is required";
              }
              if (!product.image){
                errors["imageErr"]="image is required";
            }
              setFormErrors(errors);
              const noErrors = Object.keys(errors).length === 0;
              if (noErrors) {
                const payload = {

                    productId:product.productId,
                    name: product.name,
                    price:product.price,
                    catagory: product.catagory,
                    description:product.description,
                    image:product.image
                  };
                  axios
                  .post(
                    "http://localhost:9091/product/addProduct",
                    payload
                  )
                  .then((resp) =>
                    alert("Product is saved with id:" + resp.data.productId)
                  );
                  navigate(-1);
                }
            };
























  return (
   <Container>
    <div  className="trainerbgpg" style={{marginTop:"6%"}}>
    <div  style={{fontSize:"20px"}}className="mrginlistalltrainer"> 
    <div className="col-10 col-md-8 col-lg-5">
                <div className="form-group">
    {/* <a class="previous">&laquo;</a>  */}
    {/* <Link to="/getAllGuest"  style={{color:"chocolate"}} className="previous round"><u>Back</u></Link> </div> */}
   <div>
      <label>PRODUCT NAME</label>
      <input
      id="lname"
       className="form-control"
        type="text"
        name="name"
        value={product.name}
        onChange={handleChange}
      />
       {formErrors.nameErr && (
        <div style={{ color: "red", paddingBottom: 10 }}>
          {formErrors.nameErr}
        </div>
      )}
    </div>

    <div>
        <label>PRICE</label>
        <input
        id="lname"
         className="form-control"
          type="number"
          name="price"
          value={product.price}
          onChange={handleChange}
        />
         {formErrors.priceErr && (
          <div style={{ color: "red", paddingBottom: 10 }}>
            {formErrors.priceErr}
          </div>
        )}
      </div>
      <div>
        <label>Catagory</label>
        <input
        id="lname"
         className="form-control"
          type="text"
          name="catagory"
          value={product.catagory}
          onChange={handleChange}
        />
         {formErrors.catagoryErr && (
          <div style={{ color: "red", paddingBottom: 10 }}>
            {formErrors.catagoryErr}
          </div>
        )}
      </div>
      <div>
        <label>Description</label>
        <input
        id="lname"
         className="form-control"
          type="text"
          name="description"
          value={product.description}
          onChange={handleChange}
        />
         {formErrors.descriptionErr && (
          <div style={{ color: "red", paddingBottom: 10 }}>
            {formErrors.descriptionErr}
          </div>
        )}
      </div>
      <div>
        <label>IMAGE</label>
        <input
        id="lname"
         className="form-control"
          type="text"
          name="image"
          value={product.image}
          onChange={handleChange}
        />
         {formErrors.imageErr && (
          <div style={{ color: "red", paddingBottom: 10 }}>
            {formErrors.imageErr}
          </div>
        )}
      </div>
      <br></br><br></br>
      <div id="outer" >
        <div  class="inner1"><button className = "btn btn-primary mb-2"  onClick={handleSubmit}>Save</button></div>
        {/* <div class="inner2"><Link to="/getAllGuest" className="btn btn-danger"> Cancel </Link></div> */}
      </div>
    </div>

</div>
</div>
</div>
</Container>
  )
};

export default AddProduct
