// import React from 'react'
import { React, useEffect } from "react";
// import { useSelector } from "react-redux";
import "../assets/css/style.css";
import styled from "styled-components";
import Carousel from "react-bootstrap/Carousel";

import giphy from "../assets/images/giphy.gif";
import ss from "../assets/images/ss.gif";
import { Button } from "react-bootstrap";
import { LinkContainer } from "react-router-bootstrap";
import Navbar from "./Navbar";

function Home() {
  return (
   <div>
   
    <HomeMain className="pages container">
      <MyBanner>
        <BannerSideText className="col-md-6">
          <h1><i>Shopping- Cart</i></h1>
          <span>
            <h4><i>``Happiness is not in money, but in shopping.``</i></h4>
            <h5><i>Click on 'Shop Now' and start your shopping journey with us.</i></h5>
          </span>
          <LinkContainer to={"/browse-products"}>
            <Button ><i>Shop Now</i></Button>
          </LinkContainer>
        </BannerSideText>
        <BannerSlider className="col-md-6">
          <Carousel fade>
            {/* <Carousel.Item interval={3500}>
              <img
                className="d-block w-100"
                src={ss}
                alt="First slide"
              />
            </Carousel.Item> */}
            <Carousel.Item interval={3500}>
              <img
                className="d-block w-100"
                src={giphy}
                alt="Second slide"
              />
            </Carousel.Item>
            {/* <Carousel.Item interval={1500}>
              <img
                className="d-block w-100"
                src={SignUpImage}
                alt="Third slide"
              />
            </Carousel.Item> */}
          </Carousel>
        </BannerSlider>
      </MyBanner>
    </HomeMain>
    </div>
  );
}

export default Home;
const HomeMain = styled.div``;
const MyBanner = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;

`;

const BannerSideText = styled.div`
  h1{
    color:green;
  }
  span {
    display: block;
    margin-top: 25px;
  }
  .btn {
    width: 180px;
    margin-top: 15px;
    background-color: rgb(44, 126, 124);
   
    border-color:black;
    border-radius: 25px;
    font-size: 1.2rem;
    color: black;
    :hover {
      background-color:rbg(0,0,0);
      box-shadow: none !important;
    }
  }
`;
const BannerSlider = styled.div`
 
  height: 500px;
  overflow: hidden;

  .carousel-control-next,
  .carousel-control-prev,
  .carousel-indicators {
    display: none !important;
  }

  img {
  
    height: 400px;
    width: auto;
  }
`;
