// import React from 'react'
import axios from "axios";
import { useParams } from "react-router-dom";
import React, { useEffect, useState } from "react";
import {  Container,  Row } from "react-bootstrap";
import Navbar from "./Navbar";





function ViewProduct() {
    const [product, setProduct] = useState(null);
    const { id } = useParams();
    const fetchProductById = () => {
      axios
        .get("http://localhost:9091/product/get/" + id)
        .then((resp) => setProduct(resp.data));
    };
    useEffect(fetchProductById, []);
  return (
    <Container>
   <Navbar/>
    <Row className="viewproducts">
      <div>
        <h1> <b>Product DETAILS by ID</b></h1>
        <hr />

        {product !== null && (
          <div>
            <h3 >
            ID: <b style={{ color: "rgb(79, 231, 229)" }}>  {product.productId}</b>
            </h3 >
            <h3 >
            
           Product Name : <b style={{ color: "rgb(79, 231, 229)" }}>  {product.name}</b>
            </h3>
            <h3 >
            AMOUNT : <b style={{ color:"rgb(79, 231, 229)" }}> {product.price}</b>
            </h3>
            <h3 >
             
            Catagory :  <b style={{ color: "rgb(79, 231, 229)" }}>  {product.catagory}</b>
            </h3>
            <h3 >
            Description :  <b style={{ color: "rgb(79, 231, 229)" }}>  {product.description}</b>
            </h3>
           
            image :    {product.image}
           
          </div>
        )}
      </div>
    </Row>
  </Container>
  )
}

export default ViewProduct
